import React, { useEffect } from 'react';
import Header from '../Pages/Header';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { addToCart, removeFromCart } from '../actions/cartActions';
import MessageBox from '../components/MessageBox';
import Footer from '../Pages/Footer';
import './CartScreen.css';




export default function CartScreen(props) {
  const productId = props.match.params.id;
  const qty = props.location.search
    ? Number(props.location.search.split('=')[1])
    : 1;
  const cart = useSelector((state) => state.cart);
  const { cartItems } = cart;
  const dispatch = useDispatch();
  useEffect(() => {
    if (productId) {
      dispatch(addToCart(productId, qty));
    }
  }, [dispatch, productId, qty]);

  const removeFromCartHandler = (id) => {
    // delete action
    dispatch(removeFromCart(id));
  };
  

  const checkoutHandler = () => {
    props.history.push('/signin?redirect=shipping');
  };


  
  return (
    <div>
      
      <Header/>

    <div className="row top checkOutMain">
      <div className="col-2 checkOutItems">
        <h1>Shopping Cart</h1>
        {cartItems.length === 0 ? (
          <MessageBox>
            Cart is empty. <Link to="/">Go Shopping</Link>
          </MessageBox>
        ) : (
          <ul>
            {cartItems.map((item) => (
              <li key={item.product}>
                <div className="row">

                  <div id="cartImage">
                    <img
                      src={item.image}
                      alt={item.name}
                  
                    ></img>
                  </div>
                  <div id="cartDetails">
                  <div className="min-30">
                    <Link to={`/product/${item.product}`}>{item.name}</Link>
                  </div>
                 
                    <select
                      value={item.qty}
                      onChange={(e) =>
                        dispatch(
                          addToCart(item.product, Number(e.target.value))
                        )
                      }
                    >
                      {[...Array(item.countInStock).keys()].map((x) => (
                        <option key={x + 1} value={x + 1}>
                          {x + 1}
                        </option>
                      ))}
                    </select>
                  
                    &nbsp;Rs &nbsp;{item.price}
               
                    <button
                      type="button"
                      onClick={() => removeFromCartHandler(item.product)}
                    >
                      Delete
                    </button>
                  
                  </div>
                </div>
              </li>
            ))}
          </ul>
        )}
      </div>

      
      <div className="col-1 checkoutPrice">
        <div className="card card-body" id="cardBodyCart">
          <ul id="CheckOutPriceUl">
            <li>
              <p>
                Total ({cartItems.reduce((a, c) => a + c.qty, 0)} items) : Rs &nbsp;
                {cartItems.reduce((a, c) => a + c.price * c.qty, 0)}
              </p>
            </li>
            <li>
              <button
                type="button"
                onClick={checkoutHandler}
                className="primary block"
                disabled={cartItems.length === 0}
              >
                Checkout
              </button>
            </li>
          </ul>
        </div>
        
      </div>
      
    </div>

    <Footer/>
    </div>
  );
}
